import { useState } from 'react'

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const addCount = (rating) => () => {
    setGood(good + 1)
  }

  return (
    <>
      <h1>give feedback</h1>
      <button onClick={addCount('good')} >good</button>
      <button onClick={addCount} >neutral</button>
      <button onClick={addCount} >bad</button>

      <h1>statistics</h1>
      <p>good {good}</p>
      <p>neutral {neutral}</p>
      <p>bad {bad}</p>
    </>
  )
}

export default App
