import { useState } from 'react'

const Statistics = ({good, neutral, bad}) => {

  return (
    <div>
      <h1>statistics</h1>
      <p>good {good}</p>
      <p>neutral {neutral}</p>
      <p>bad {bad}</p>
    </div>
  )
}


const App = () => {
